import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import Navbar from 'features/navbar';
import FriendListWidget from 'features/widgets/FriendListWidget';
import MyPostWidget from 'features/widgets/MyPostWidget';
import PostsWidget from 'features/widgets/PostsWidget';
import UserWidget from 'features/widgets/UserWidget';
import { Box, useMediaQuery } from '@mui/material';

const ProfilePage = () => {
	const [user, setUser] = useState(null);
	const { userId } = useParams();
	const token = useSelector((state) => state.token);
	const isNonMobileScreens = useMediaQuery('(min-width:1000px)');

	const getUser = async () => {
		const response = await fetch(
			`https://socialsocial.onrender.com/users/${userId}`,
			{
				method: 'GET',
				headers: { Authorization: `Bearer ${token}` },
			}
		);
		const data = await response.json();
		setUser(data);
	};

	useEffect(() => {
		getUser();
	}, []); // eslint-disable-line react-hooks/exhaustive-deps

	if (!user) return null;

	return (
		<Box>
			<Navbar />
			<Box
				width='100%'
				padding='2rem 6%'
				display={isNonMobileScreens ? 'flex' : 'block'}
				gap='2rem'
				justifyContent='center'
			>
				<Box flexBasis={isNonMobileScreens ? '26%' : undefined}>
					<UserWidget userId={userId} picturePath={user.picturePath} />
					<Box m='2rem 0' />
					<FriendListWidget userId={userId} />
				</Box>
				<Box
					flexBasis={isNonMobileScreens ? '42%' : undefined}
					mt={isNonMobileScreens ? undefined : '2rem'}
				>
					<MyPostWidget picturePath={user.picturePath} />
					<Box m='2rem 0' />
					<PostsWidget userId={userId} isProfile />
				</Box>
			</Box>
		</Box>
	);
};

export default ProfilePage;
